// Modal slider
$(function(){
	
	// Функция активации слайдера
	$.modalSlider = function(item){
		var sliderFlower = new Swiper('.modal-catalog_slider-' + item,{
			slidesPerView:1
			,spaceBetween:0
			,autoplay:3000
			,speed:300
			,loop:true
			,prevButton:'.modal-catalog_slider-' + item + '_arrow-left'
			,nextButton:'.modal-catalog_slider-' + item + '_arrow-right'
		});
	};

	// Активируем все существующие слайдеры
	for (var i = $('.modal-catalog_slider').length - 1; i >= 0; i--) {
		$.modalSlider(i);
	}

	// Убираем стрелочки слайдера, если слайдов меньше 2
	$('.modal-catalog_slider-wrapper').each(function(index,item){
		if($(item).children().length<2){
			$(item).parents('.modal-catalog_slider-w').addClass('noArrow');
		}
	});

});


