// Modal Schedule • показ маршрута по нажатии на номер
$('.schedule_nav_link').on('click',function(){
	$(this).parents('.schedule_nav').find('.schedule_nav_link').removeClass('active');
	$(this).addClass('active');
	$(this).parents('.schedule_section').find('.schedule_det_item').removeClass('active').height(0);
	$(this).parents('.schedule_section').find('.schedule_det_item-' + $(this).attr('data-number')).addClass('active').height($(this).parents('.schedule_section').find('.schedule_det_item-' + $(this).attr('data-number') + ' .schedule_det_item-in').outerHeight() + 'px');
	var scrollPoint = $(this).parents('.schedule_section').offset().top + $('.modal-schedule').scrollTop() - 50;

	return false;
});

$('.schedule_det_table_item_p-title').each(function(index){
	var width = $(this).innerWidth(),
		parentIndex = $(this).index() + 1;
	$(this).parents('.schedule_det_table').find('.schedule_det_table_item_p:nth-child(' + parentIndex + ')').innerWidth(width);
});




